mapper.select:
SELECT FAQ_NO, QUESTION, ANSWER, FAQ_TYPE, HITS, DISPLAY_YN, DISPLAY_TOP_YN, REG_DT, MOD_DT FROM CUSTOMER_FAQ
ORDER BY FAQ_NO DESC
LIMIT #{page.startNo}, #{page.recordCount}

SELECT  FAQ_NO, QUESTION, ANSWER, FAQ_TYPE, HITS, DISPLAY_YN, DISPLAY_TOP_YN, REG_DT, MOD_DT
FROM    CUSTOMER_FAQ
WHERE   FAQ_NO = #{faqNo}

SELECT  COUNT(*)
FROM    CUSTOMER_FAQ
WHERE   FAQ_TYPE = #{faqType}
AND     DISPLAY_TOP_YN = 'Y'

    AND FAQ_NO != ${faqNo}
